INSERT INTO `game` VALUES (1, 'PLAYERUNKNOWN\'S BATTLEGROUNDS', '[\"射击\",\"多人\"]', 'PUBG Corporation', 'PUBG Corporation', '2017-12-21', 98.00, 0.00, 9.5, '[\"中文\",\"英文\"]', '绝地求生(PLAYERUNKNOWN’S BATTLEGROUNDS)是战术竞技类型的游戏，每一局游戏将有100名玩家参与，他们将被投放在绝地岛(battlegrounds)的上空，游戏开始跳伞时所有人都一无所有。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256773105/movie480.webm?t=1579721657;https://media.st.dl.pinyuncloud.com/steam/apps/256793553/movie480_vp9.webm?t=1595992300', 'https://media.st.dl.pinyuncloud.com/steam/apps/578080/ss_606cee13e97530720c678513cb1138ef9854d7d5.600x338.jpg?t=1596674062;', 'https://media.st.dl.pinyuncloud.com/steam/apps/578080/header.jpg?t=1596674062', '', '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (2, 'Fall Guys: Ultimate Knockout', '[\"休闲\",\"多人\"]', 'Mediatonic', 'Devolver Digital', '2020-08-04', 58.00, 1.00, 9.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '《糖豆人: 终极淘汰赛》是一款多人乱斗的闯关综艺游戏，这里可以亲身体验综艺闯关的刺激！在最多可容纳60人的线上竞技场内，一轮又一轮敌我难分的比赛中，刺激和乱斗将会无限升级！', 'https://media.st.dl.pinyuncloud.com/steam/apps/256775534/movie480_vp9.webm?t=1596592514', 'https://media.st.dl.pinyuncloud.com/steam/apps/1097150/ss_b2b6b170330b8af1f50d0e90efad984adafeb281.600x338.jpg?t=1597424899;https://media.st.dl.pinyuncloud.com/steam/apps/1097150/ss_dffa1e7c9c67f84b10a48481713f15d1016698b5.600x338.jpg?t=1597424899', 'https://media.st.dl.pinyuncloud.com/steam/apps/1097150/header.jpg?t=1597424899', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (3, 'World of Warships', '[\"休闲\",\"多人\",\"免费\"]', 'Wargaming Group Limited', 'Wargaming Group Limited', '2017-11-15', 0.00, 1.00, 10.0, '[\"中文\",\"英文\",\"法语\"]', '《战舰世界》将让您免费体验到史诗级的海战场面、300多艘可选的历史经典战舰、策略性和即时战斗游戏性达到完美平衡的线上对战。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256793113/movie480_vp9.webm?t=1594980050', 'https://media.st.dl.pinyuncloud.com/steam/apps/552990/ss_43ac66ca58cbb5c2665c43bcd979016757e7822b.600x338.jpg?t=1597765218', 'https://media.st.dl.pinyuncloud.com/steam/apps/552990/header.jpg?t=1597765218', NULL, '[\"windows\", \"macos\"]', '[1,2]');
INSERT INTO `game` VALUES (4, 'NBA 2K20', '[\"体育\",\"多人\",\"休闲\",\"模拟\"]', 'Visual Concepts', '2KJump King', '2019-09-05', 100.00, 1.00, 7.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '2K continues to redefine what’s possible in sports gaming with NBA 2K20, featuring best in class graphics & gameplay, ground breaking game modes, and unparalleled player control and customization.', 'https://media.st.dl.pinyuncloud.com/steam/apps/256761260/movie480.webm?t=1567806881', 'https://media.st.dl.pinyuncloud.com/steam/apps/1089350/ss_67c35857eaf92741a503b2c021f67fbc27d29b33.600x338.jpg?t=1595623296', 'https://media.st.dl.pinyuncloud.com/steam/apps/1089350/header.jpg?t=1595623296', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (5, 'Dota 2', '[\"策略\",\"多人\",\"免费\"]', 'Valve', 'Valve', '2013-07-09', 0.00, 1.00, 5.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '每一天全球有数百万玩家化为一百余名Dota英雄展开大战。不论是游戏时间刚满10小时还是1000小时，比赛中总能找到新鲜感。定期的更新则保证游戏性、功能和英雄都能持续发展，Dota 2已真正地焕发了生命。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256692021/movie480.webm?t=1554408539', 'https://media.st.dl.pinyuncloud.com/steam/apps/570/ss_86d675fdc73ba10462abb8f5ece7791c5047072c.600x338.jpg?t=1591047995', 'https://media.st.dl.pinyuncloud.com/steam/apps/570/header.jpg?t=1591047995', NULL, '[\"windows\", \"macos\"]', '[1,2]');
INSERT INTO `game` VALUES (6, '人类一败涂地 / Human Fall Flat', '[\"休闲\",\"多人\",\"冒险\"]', 'No Brakes Games', 'Curve Digital', '2016-07-23', 48.00, 1.00, 1.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '《人类一败涂地》是一款轻松搞笑的沙雕游戏，游戏的场景发生在飘渺的梦境之中。玩家既可以单机自闭，也可以最多八人联机一起欢畅。游戏支持的创意工坊，还可以让大家免费玩到不计其数的高质量玩家关卡和皮肤。游戏支持中文。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256785875/movie480_vp9.webm?t=1590080915', 'https://media.st.dl.pinyuncloud.com/steam/apps/477160/ss_72d225da96b7f05047ad9ac9695acb4189a32971.600x338.jpg?t=1597785161', 'https://media.st.dl.pinyuncloud.com/steam/apps/477160/header_alt_assets_8_schinese.jpg?t=1597785161', NULL, '[\"windows\", \"macos\"]', '[1,2]');
INSERT INTO `game` VALUES (7, '七龙珠Z 卡卡洛特', '[\"角色扮演\",\"多人\",\"动作\"]', 'CyberConnect2 Co. Ltd.', 'BANDAI NAMCO Entertainment', '2020-01-17', 298.00, 0.80, 0.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '《七龙珠Z 卡卡洛特》里可体验到悟空及其他Z战士的故事！除了史诗级的对战外，在七龙珠Z世界里亦可以与悟空、悟饭及贝吉达等等角色进行打斗、钓鱼、用餐及训练。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256773292/movie480.webm?t=1579881659', 'https://media.st.dl.pinyuncloud.com/steam/apps/851850/ss_99df46ea315597e06b62dfe15107db7b7c8ed1cb.600x338.jpg?t=1592517730', 'https://media.st.dl.pinyuncloud.com/steam/apps/851850/header.jpg?t=1592517730', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (8, 'Assassin\'s Creed® Odyssey', '[\"角色扮演\",\"多人\",\"动作\",\"单人\"]', 'Ubisoft Quebec, Ubisoft Montreal, Ubisoft Bucharest, Ubisoft Sin', 'Ubisoft', '2018-10-06', 298.00, 1.00, 10.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '在《Assassin\'s Creed® Odyssey》当中主宰自己的命运。 从一名流浪者蜕变成传奇人物，踏上这趟奥德赛之旅，找出你过往的秘密并改变古希腊的命运。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256730989/movie480.webm?t=1538496199', 'https://media.st.dl.pinyuncloud.com/steam/apps/812140/ss_0ef33c0f230da6ebac94f5959f0e0a8bbc48cf8a.600x338.jpg?t=1595367552', 'https://media.st.dl.pinyuncloud.com/steam/apps/812140/header_schinese.jpg?t=1595367552', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (9, '港诡实录', '[\"独立\",\"冒险\",\"动作\",\"单人\"]', 'Ghostpie Studio', 'Ghostpie Studio, Gamera Game', '2020-01-06', 56.00, 1.00, 10.0, '[\"中文\",\"英文\"]', '《港诡实录》是一款以中国香港都市传说为题材的第一人称恐怖冒险游戏。故事讲述一群年轻人为了录制探灵节目“逃出灵界点”，来到九龙城寨遗址，然而这次的录制却因为一些突发事件变得诡异起来……', 'https://media.st.dl.pinyuncloud.com/steam/apps/256765753/movie480.webm?t=1572488956', 'https://media.st.dl.pinyuncloud.com/steam/apps/1178490/ss_f5d8eae635d250fd19e957c578e8ffbeaa37052b.600x338.jpg?t=1593679601', 'https://media.st.dl.pinyuncloud.com/steam/apps/1178490/header.jpg?t=1593679601', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (10, 'Jump King', '[\"独立\",\"冒险\",\"动作\",\"单人\"]', 'Nexile', 'Nexile', '2019-05-06', 49.00, 1.00, 10.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '跳王平台高且险，为寻辣妹力争先。挑战勇者请小心，一失足成千古恨！', 'https://media.st.dl.pinyuncloud.com/steam/apps/256752865/movie480.webm?t=1559927068', 'https://media.st.dl.pinyuncloud.com/steam/apps/1061090/ss_57eb51f796849ef3338d4d884d6f7ed4b2d0fbd8.600x338.jpg?t=1593098983', 'https://media.st.dl.pinyuncloud.com/steam/apps/1061090/header_alt_assets_3.jpg?t=1593098983', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (11, '无主之地3', '[\"独立\",\"休闲\"]', 'GearBox SoftWare', '2K', '2020-03-14', 99.00, 1.00, 10.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', 'The original shooter-looter returns', '', 'https://media.st.dl.pinyuncloud.com/steam/apps/397540/ss_3531d9f91265d94fc06f6587eba1ca49f2c423d1.600x338.jpg?t=1596687250', 'https://media.st.dl.pinyuncloud.com/steam/apps/397540/header.jpg?t=1596687250', NULL, '[\"windows\", \"linux\",\"macos\"]', '[1,2]');
INSERT INTO `game` VALUES (12, 'GreedFall', '[\"角色扮演\",\"单人\",\"开放世界\"]', 'Spiders', 'Focus Home Interactive', '2019-09-10', 158.00, 1.00, 10.0, '[\"中文\",\"英语\"]', '感受核心玩家的角色扮演体验，在一片充满魔力、财富、未解之谜和奇异生物的土地上，塑造一个崭新的世界，决定它的命运走向。使用外交艺术、谎言骗局和铁腕手段，成为这个充满活力的动态世界的一部分——影响它的演变进程，书写你的故事。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256768212/movie480.webm?t=1574852767', '', 'https://media.st.dl.pinyuncloud.com/steam/apps/606880/ss_fc324dd18b9da108e70b40fe8dd65970e11e471f.600x338.jpg?t=1575302145', NULL, '[\"windows\", \"linux\"]', '[1,2]');
INSERT INTO `game` VALUES (13, 'Rogue Legacy 2', '[\"独立\",\"动作\",\"冒险\"]', 'Cellar Door Games', 'Cellar Door Games', '2020-08-19', 70.00, 0.50, 10.0, '[\"中文\",\"英文\"]', 'Rogue Legacy 2 is what you\'d get if you mashed Rogue Legacy and a sequel together. Every time you die, your children will succeed you, and each child is unique. Your daughter might be a Colourblind Archer, and your son could be a Pacifistic Chef. Either', 'https://media.st.dl.pinyuncloud.com/steam/apps/256790428/movie480_vp9.webm?t=1592931937', 'https://media.st.dl.pinyuncloud.com/steam/apps/1253920/ss_45a3b616aed1822983e1127f9cc3f70dbd0c2248.116x65.jpg?t=1597775096', 'https://media.st.dl.pinyuncloud.com/steam/apps/1253920/ss_20726e2efa412884e9a7e1b053355e8311e4dffb.116x65.jpg?t=1597775096', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (14, '层层梦境', '[\"动作\",\"单人\",\"剧情\",\"动作\"]', 'Afterburner Studios', 'Freedom! Games, Maple Whisperi', '2020-08-14', 69.00, 1.00, 10.0, '[\"中文\",\"英文\"]', '《层层梦境》是一款节奏紧张，画面流畅，动作丰富的RPG游戏。每一次睡眠即把玩家带入一个不同的地牢，而你必须释放梦境的力量才能看到崭新的一天。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256791226/movie480_vp9.webm?t=1597339249', 'https://media.st.dl.pinyuncloud.com/steam/apps/1040420/ss_06e07bb834ccfd9ba630e64d2984f9230aec5394.116x65.jpg?t=1597627482', 'https://media.st.dl.pinyuncloud.com/steam/apps/1040420/ss_ce82f467b8cb4686c98cfe317a27d7e79689d24c.116x65.jpg?t=1597627482', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (15, 'Risk of Rain 2', '[\"第三人称\",\"射击\",\"动作\",\"多人\",\"动作\",\"合作\"]', 'Hopoo Games', 'Gearbox Publishing', '2020-08-11', 80.00, 1.00, 10.0, '[\"中文\",\"英文\"]', '浴血奋战杀出暴戾的怪物重围，在混乱的外星球上逃过劫难。可孤军奋战，也可与好友并肩作战。 以出人意料的方式合成战利品，同时精通每个角色，直至您自己成为初次迫降时曾颇为惧怕的恐怖威胁。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256795993/movie480_vp9.webm?t=1597157826', 'https://media.st.dl.pinyuncloud.com/steam/apps/632360/ss_2bb49071317f7b241a527cf6e7aabd2cb6af055b.600x338.jpg?t=1597330177', 'https://media.st.dl.pinyuncloud.com/steam/apps/632360/ss_a3f57f281813cb51cb5d919701470acb962ff297.600x338.jpg?t=1597330177', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (16, 'ANAREA Battle Royale', '[\"动作\",\"独立\"]', 'AXC GAMES', 'AXC GAMES', '2020-08-14', 62.00, 1.00, 10.0, '[\"中文\",\"英文\"]', 'ANAREA Battle Royale is the first project of AXC Games indie studio. ANAREA is a fast-paced Third Person Shooter Battle Royale arcady game aiming to be dynamic and competitive.', 'https://media.st.dl.pinyuncloud.com/steam/apps/256794504/movie480_vp9.webm?t=1596043673', 'https://media.st.dl.pinyuncloud.com/steam/apps/1103950/ss_019ea48800b7f7fe685d937e579693c5fec75ab5.116x65.jpg?t=1597406366', 'https://media.st.dl.pinyuncloud.com/steam/apps/1103950/ss_95f5dcc2cca41a1a643dc248872debe152af6ebb.116x65.jpg?t=1597406366', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (17, 'Ever Forward', '[\"独立\",\"冒险\",\"单人\",\"休闲\",\"策略\"]', 'Pathea Games', 'Pathea Games', '2020-08-13', 58.00, 1.00, 10.0, '[\"英文\"]', '《永进》是一款3D解密游戏，游戏讲述了少女小雅的故事。她独自生活在一座孤岛上，突然有一日被拉入了充满着诡异气息的异空间之中，各种复杂困难的谜题接踵而至，她不得不迎面而上。随着她的前进，她的记忆渐渐复苏，她为何独自一人？她到底身在何方一切？关于这个世界的关于自身的谜题终将被慢慢解开', 'https://media.st.dl.pinyuncloud.com/steam/apps/256796415/movie480_vp9.webm?t=1597341774', 'https://media.st.dl.pinyuncloud.com/steam/apps/1220370/ss_aada1a906b7cec7283ed57cad26a75aac3f2e200.600x338.jpg?t=1597651403', 'https://media.st.dl.pinyuncloud.com/steam/apps/1220370/ss_203b3190c77a939a9353bca10c401b7c141be087.600x338.jpg?t=1597651403', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (18, 'There Is No Game : Wrong Dimension', '[\"独立\",\"冒险\",\"休闲\",\"点击\"]', 'Draw Me A Pixel', 'Draw Me A Pixel', '2020-08-07', 43.00, 1.00, 10.0, '[\"中文\",\"英文\"]', '这里没有游戏。所以不要东捣弄西捣弄，到处点点点的。你不想被踢出你的视频游戏世界，对吧？当然不想！', 'https://media.st.dl.pinyuncloud.com/steam/apps/256793611/movie480_vp9.webm?t=1595513367', 'https://media.st.dl.pinyuncloud.com/steam/apps/1240210/ss_8c20dd0ab3a824c27fae5b2c921a3984d1821a34.600x338.jpg?t=1597694171', 'https://media.st.dl.pinyuncloud.com/steam/apps/1240210/ss_1b523c5986c28eae7eddad3880ff45f242c2f280.600x338.jpg?t=1597694171', NULL, '[\"windows\",\"macos\"]', '[1,2]');
INSERT INTO `game` VALUES (19, 'Spiritfarer®', '[\"建造\",\"冒险\",\"独立\"]', 'Thunder Lotus Games', 'Thunder Lotus Games', '2020-08-18', 92.00, 1.00, 10.0, '[\"英文\"]', '《Spiritfarer®》是一款关于死亡的休闲管理游戏。作为逝者的船主，建造一艘船去探索世界，关爱你的幽灵朋友，引导他们穿越神秘的海洋，最终将他们送到来世。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256797231/movie480_vp9.webm?t=1597770054', 'https://media.st.dl.pinyuncloud.com/steam/apps/972660/ss_a751970e68bc928538fbc4f52d6090c6ff1d8974.600x338.jpg?t=1597790064', 'https://media.st.dl.pinyuncloud.com/steam/apps/972660/ss_6ae155cb7c5f65f46a8f0dc43ba57aff8f57616c.600x338.jpg?t=1597790064', NULL, '[\"windows\",\"macos\",\"linux\"]', '[1,2]');
INSERT INTO `game` VALUES (20, 'COCKHEAD', '[\"独立\",\"冒险\",\"动作\",\"休闲\"]', 'Yoba Games', 'Yoba Games', '2020-08-16', 28.00, 1.00, 10.0, '[\"英文\"]', '“COCKHEAD” 给我们讲述关于一的故事阴茎，关于一个能够成功的人。 与我们故事的主人工一起通过所有怵目惊心的奇遇和冒险， 享受全部手绘的图像、独创的情节和经典著名的爵士音乐。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256767510/movie480.webm?t=1581686524', 'https://media.st.dl.pinyuncloud.com/steam/apps/1172050/ss_8bde40db4cb48646d98509d5ff9694cb5beec781.600x338.jpg?t=1597588710', 'https://media.st.dl.pinyuncloud.com/steam/apps/1172050/ss_d7343875006d8b7e2520f133500012fc2dcab32c.600x338.jpg?t=1597588710', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (21, 'SMITE®', '[\"策略\",\"多人\",\"免费\"]', 'Titan Forge Games', 'Hi-Rez Studios', '2015-09-08', 0.00, 1.00, 10.0, '[\"中文\",\"英文\",\"法语\",\"意大利语\"]', '和三千五百多万名玩家一起在众神的战场中战个痛快！挥舞托尔的雷神之锤；以美杜莎之力石化你的敌人；你也可以从超过100位神话人物中选择你想发挥的神圣之力。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256787942/movie480_vp9.webm?t=1591626576', 'https://media.st.dl.pinyuncloud.com/steam/apps/386360/ss_03041b0222a512005b1f38ac0da7f42cc4c1283f.600x338.jpg?t=1597150504', 'https://media.st.dl.pinyuncloud.com/steam/apps/386360/header_schinese.jpg?t=1597150504', NULL, '[\"windows\"]', '[1,2]');
INSERT INTO `game` VALUES (22, 'Plants vs. Zombies GOTY Edition', '[\"策略\",\"单人\",\"休闲\"]', 'PopCap Games, Inc.', 'PopCap Games, Inc., Electronic Arts', '2009-05-06', 21.00, 1.00, 0.0, '[\"中文\",\"英文\"]', '来自曾开发过宝石迷阵和幻幻球的宝开公司的全新动作策略游戏！ 可怕的僵尸即将入侵你的家，唯一的防御方式就是你栽种的植物！武装你的植物，切换他们不同的功能，诸如强悍的豌豆射手或樱桃炸弹，更加快速有效的将僵尸阻挡在入侵的道路上。不同的敌人，不同的玩法构成五种不同的游戏模式.', 'https://media.st.dl.pinyuncloud.com/steam/apps/900829/movie480.webm?t=1584811605', 'https://media.st.dl.pinyuncloud.com/steam/apps/3590/0000008150.600x338.jpg?t=1584811616', 'https://media.st.dl.pinyuncloud.com/steam/apps/3590/header.jpg?t=1584811616', NULL, '[\"windows\", \"macos\"]', '[1,2]');
