INSERT INTO `game` VALUES (37, 'からかい上手の高木さんVR 1学期', '[\"休闲\",\"模拟\"]', 'KAYAC Inc.', 'KAYAC Inc.', '2020-05-21', 47.00, 1.00, 0.0, '[\"日文\"]', 'Virtually experience the playful and relentless teasing of heroine, Takagi-san, in Teasing Master Takagi-san VR - 1st Semester (Karakai Jouzu no Takagi-san VR). With sales of the original comic series exceeding 8.2 million copies in Japan, the romantic', 'https://media.st.dl.pinyuncloud.com/steam/apps/256778154/movie480.webm?t=1586431352', 'https://media.st.dl.pinyuncloud.com/steam/apps/1264160/ss_804f47fd375ba29da7ddbfc02b830bfa7086a988.600x338.jpg?t=1591768483', 'https://media.st.dl.pinyuncloud.com/steam/apps/1264160/header.jpg?t=1591768483', NULL, '[\"windows\"]', '[1,2]');
