INSERT INTO `game` VALUES (39, 'Rock of Ages 3: Make & Break', '[\"动作\",\"独立\",\"策略\"]', 'ACE TeamGiant，Monkey Robot', 'Modus Games, 3goo', '2020-07-21', 88.00, 1.00, 0.0, '[\"英文\",\"法语\",\"意大利语\",\"中文\"]', 'Rock of Ages 3: Make & Break是一款竞技塔防和街机动作游戏，融合了ACE Team搞笑的Monty Python-esque幽默，新奇好玩脑洞大开。', 'https://media.st.dl.pinyuncloud.com/steam/apps/256794640/movie480_vp9.webm?t=1596508126', 'https://media.st.dl.pinyuncloud.com/steam/apps/1101360/ss_e6cec53d51122e66587ec309ffe8453952a50394.600x338.jpg?t=1596508131', 'https://media.st.dl.pinyuncloud.com/steam/apps/1101360/header.jpg?t=1596508131', NULL, '[\"windows\"]', '[1,2]');
